        setOpenModalValidation(false)
    }

    function myStopFunction() {
        clearTimeout(closeModal);
      }
    

    return (
        <div className='formulaire-wrapper'>
            <div className='formulaire-wrapper'>
                <h1> Félicitation votre demande a bien été envoyé</h1>
            </div>
        </div>
    )
}

export default Validation
