import React, { useRef, useState, useEffect } from 'react'
import logoValidation from '../Assets/logoValidation.svg'
import './Questions.css'

const Validation = ({setOpenModalValidation}) => {

    const closeModal = () => {
        setOpenModalValidation(false)
    }

    // useEffect(() => {
    //     setTimeout(closeModal, 2000)
    // }, [])

    return (
        <div className='formulaire-wrapper'>
            <div className='validation'>
                <h1> Félicitation votre demande a bien été envoyé</h1>
                <img src={logoValidation} alt='Validation du formumaire'/>
            </div>
        </div>
    )
}

export default Validation
