import React, { useRef, useState, useEffect } from 'react'
import './Questions.css'
import questionsData from '../Data/data.json'
import { Footer } from './Footer'
import Compteur from './Compteur'
import closeButton from '../Assets/closeButton.svg'
import { useForm } from 'react-hook-form';

const Questions = ({setOpen}) => {

    
    const { register, handleSubmit } = useForm()

    const [activeQuestion, setActiveQuestion] = useState(0);
    const data = questionsData.data[activeQuestion]
    const length = data.choices.length
    const numberOfQuestions = questionsData.data.length

    const [selected, setSelected] = useState('');
    const [error, setError] = useState('');
    const [inputValue, setInputValue] = useState({
        input1: "",
        input2:""
    })
    const radiosWrapper = useRef();
  
    

    useEffect(() => {
      const findCheckedInput = data.inputType === "radio" && radiosWrapper.current.querySelector('input:checked');
      if(findCheckedInput) {
        findCheckedInput.checked = false;
      }
    }, [data]);
  
    // const changeRadioHandler = (e) => {
    //     setSelected(e.target.value);
    //     if(error) {
    //         setError('');
    //     }
    // };

    const closeModal = () => {
        setOpen(false)
    };

    const onSubmit = () => {

    }

    return (
        <div className='formulaire-wrapper'>
            <form className='formulaire' onSubmit={handleSubmit(onSubmit)}>
                <div className='question__title'>
                    <h2>{data.titre}</h2>
                    {data.description && <p>{data.description}</p>}
                </div>

                {/* input radio */}
                {data.inputType === "radio" && <div className={`${length === 2 ? 'question__choices' : 'question__choices--small-card'} control`} ref={radiosWrapper}>
                    {data.choices.map((choice, index) => (
                        <div className={`${length === 2 ? 'question__choices__choice' : 'question__choices__choice--small-card'}`} key={index}>
                            <label htmlFor={choice.name}>
                                {choice.image && <img className='radio-image' src={choice.image} alt={choice.name}/>}
                                <p>{choice.name}</p>
                                <input className='radio' type="radio" name="answer" id={choice.name} value={choice.name} {...register(data.question)}/>
                            </label>
                        </div>
                    ))}
                </div>}

                {data.inputType !== "radio" && (
                    <>
                        <input type={data.inputs[0].inputType} placeholder={data.inputs[0].placeholder} value={inputValue.input1} {...register(data.question)}/>
                        {data.inputs[1] && <input type={data.inputs[1].inputType} placeholder={data.inputs[1].placeholder} value={inputValue.input2} {...register(data.question)}/>}
                    </>
                )}
                
                <Footer data={data} numberOfQuestions={numberOfQuestions} error={error} setError={setError} activeQuestion={activeQuestion} onSetActiveQuestion={setActiveQuestion} selected={selected} setSelected={setSelected} inputValue={inputValue} setInputValue={setInputValue}/>

                <Compteur activeQuestion={activeQuestion} numberOfQuestions={numberOfQuestions}/>

                <div className='closeButton' onClick={closeModal}><img src={closeButton} alt='fermeture modale'/></div>
            </form>
        </div>
    )
}

export default Questions
