import React, { useRef, useState, useEffect } from 'react'
import './Questions.css'
import questionsData from '../Data/data.json'
import { Footer } from './Footer'
import Compteur from './Compteur'
import closeButton from '../Assets/closeButton.svg'
import { set, useForm } from 'react-hook-form';

const Questions = ({setOpen}) => {

    
    //const { register, handleSubmit } = useForm()

    const [activeQuestion, setActiveQuestion] = useState(0);
    const data = questionsData.data[activeQuestion]
    const length = data.choices.length
    const numberOfQuestions = questionsData.data.length

    const [inputValue, setInputValue] = useState('');
    const [selected, setSelected] = useState('');
    const [error, setError] = useState('');
    const radiosWrapper = useRef();
    

    useEffect(() => {
      const findCheckedInput = data.inputType === "radio" && radiosWrapper.current.querySelector('input:checked');
      if(findCheckedInput) {
        findCheckedInput.checked = false;
      }
    }, [data]);
    
    const changeRadioHandler = (e) => {
        setSelected(e.target.value);
        if(error) {
            setError('');
        }
    };

    const changeInput1Handler = (e) => {
        setSelected(e.target.value);
        if(error) {
            setError('');
        }
    };

    const changeInput2Handler = (e) => {
        setSelected2(e.target.value);
        if(error) {
            setError('');
        }
    };

    const closeModal = () => {
        setOpen(false)
    };

    return (
        <div className='formulaire-wrapper'>
            <form className='formulaire'>
                <div className='question__title'>
                    <h2>{data.titre}</h2>
                    {data.description && <p>{data.description}</p>}
                </div>

                {/* input radio */}
                {data.inputType === "radio" && <div className={`${length === 2 ? 'question__choices' : 'question__choices--small-card'} control`} ref={radiosWrapper}>
                    {data.choices.map((choice, index) => (
                        <div className={`${length === 2 ? 'question__choices__choice' : 'question__choices__choice--small-card'}`} key={index}>
                            <label htmlFor={choice.name}>
                                {choice.image && <img className='radio-image' src={choice.image} alt={choice.name}/>}
                                <p>{choice.name}</p>
                            </label>
                            <input className='radio' type="radio" name="answer" id={choice.name} value={choice.name} onChange={changeRadioHandler} />
                        </div>
                    ))}
                </div>}

                {data.inputType !== "radio" && (
                    <>
                        <input type={data.inputs[0].inputType} placeholder={data.inputs[0].placeholder} onChange={changeInput1Handler} value={inputValue.input1} />
                        {/* En cas de 2eme champ à compléter */}
                        {data.inputs[1] && <input type={data.inputs[1].inputType} placeholder={data.inputs[1].placeholder} onChange={changeInput2Handler} value={inputValue.input2}/>}
                    </>
                )}
                
                <Footer data={data} numberOfQuestions={numberOfQuestions} error={error} setError={setError} activeQuestion={activeQuestion} onSetActiveQuestion={setActiveQuestion} selected={selected} setSelected={setSelected} inputValue={inputValue} setInputValue={setInputValue} setAnswer={setAnswer}/>

                <Compteur activeQuestion={activeQuestion} numberOfQuestions={numberOfQuestions}/>

                <div className='closeButton' onClick={closeModal}><img src={closeButton} alt='fermeture modale'/></div>
            </form>
        </div>
    )
}

export default Questions
